CREATE TABLE orders (
  orderid BIGSERIAL NOT NULL,
  orderdate timestamp(0) NOT NULL,
  deliverydate timestamp(0) NOT NULL,
  deliveryloca varchar(200) NOT NULL,
  username varchar(20) NOT NULL,
  totalprice bigint NOT NULL,
