CREATE TABLE product (
  proid varchar(10) NOT NULL,
  proname varchar(30) NOT NULL,
  price bigint NOT NULL,
  oldprice bigint NOT NULL,
  smalldesc varchar(1000) NOT NULL,
  detaildesc text NOT NULL,
  prodate timestamp(0) NOT NULL,
  proqty int NOT NULL,
  proimage varchar(200) NOT NULL,
  catid varchar(10) NOT NULL,
  supid varchar(10) NOT NULL,
  branchid varchar(10) NOT NULL,
  PRIMARY KEY (proid, branchid),
  FOREIGN KEY (catid) REFERENCES category (catid),
  FOREIGN KEY (supid) REFERENCES suppliers (supid),
  FOREIGN KEY (branchid) REFERENCES branch (branchid)
) ;



CREATE TABLE orderdetail (
  orderid int NOT NULL,
